mapper.select:
SELECT COUNT(note_id)
FROM collection_note cn
LEFT JOIN collection c ON cn.collection_id = c.collection_id
WHERE c.creator_id = #{creatorId}
  AND cn.note_id   = #{noteId}

mapper.update:
UPDATE collection
name = #{name}, name_en = #{nameEn}, description = #{description},
WHERE collection_id = #{collectionId}
