mapper.select:
SELECT note_id, author_id, question_id, COALESCE(content_en, content) AS content, like_count, comment_count, collect_count, created_at, updated_at FROM note
ORDER BY created_at
${params.order}
ASC
LIMIT #{limit} OFFSET ${offset}

select.if:
SELECT question_id
FROM note
WHERE author_id = #{authorId}
  AND question_id IN

    #{questionId}

mapper.update:
UPDATE note
SET content = #{content}
WHERE note_id = #{noteId}

UPDATE note
SET like_count = like_count + 1
WHERE note_id = #{noteId}

UPDATE note
SET like_count = like_count - 1
WHERE note_id = #{noteId}

UPDATE note
SET collect_count = collect_count + 1
WHERE note_id = #{noteId}

UPDATE note
SET collect_count = collect_count - 1
WHERE note_id = #{noteId}
