where.if:
(category_id = #{queryParam.categoryId}
 OR category_id IN (
     SELECT category_id FROM category
     WHERE parent_category_id = #{queryParam.categoryId}
 ))

mapper.update:
UPDATE question
title = #{question.title}, title_en = #{question.titleEn}, difficulty = #{question.difficulty}, exam_point = #{question.examPoint},
WHERE question_id = #{question.questionId}

UPDATE question
SET view_count = view_count + 1
WHERE question_id = #{questionId}

delete.if:
DELETE FROM question
WHERE category_id IN

    #{categoryId}
